mapper.select:
SELECT t1.id, t1.order_id, t1.item_id, t1.item_name, t1.item_num, t1.item_icon, t1.item_price, t1.total_price, t2.item_no, t2.spec, t2.factory, t2.qty, DATE_ADD(t3.produced_time, INTERVAL t2.expiry_date MONTH) AS validDay, IFNULL(t5.item_num, 0) AS refund_num, IFNULL(t5.total_price, 0) AS refund_price FROM t_order_info AS t1 JOIN t_item AS t2 ON t2.id = t1.item_id JOIN t_item_batch AS t3 ON t3.item_no = t2.item_no AND t3.batch_no = t1.item_batch_no LEFT JOIN t_refund_order AS t4 ON t4.order_id = t1.order_id AND t4.status = 6 LEFT JOIN t_refund_order_info AS t5 ON t5.refund_order_id = t4.id AND t5.item_id = t1.item_id
1 = 1
AND t1.order_id = #{orderId}

SELECT t1.item_id, t1.item_name, t1.item_num, t1.item_icon, t1.item_price, t2.item_no, t2.spec, t2.factory, t2.qty, DATE_ADD(t3.produced_time, INTERVAL t2.expiry_date MONTH) AS validDay FROM t_order_info AS t1 JOIN t_item AS t2 ON t2.id = t1.item_id JOIN t_item_batch AS t3 ON t3.item_no = t2.item_no AND t3.batch_no = t1.item_batch_no
1 = 1
AND t1.order_id = #{orderId}
